mapper.select:
SELECT
-- 级联表的字段 ,yisheng.yisheng_uuid_number as yishengUuidNumber ,yisheng.yisheng_name as yishengName ,yisheng.yisheng_photo as yishengPhoto ,yisheng.yisheng_types as yishengTypes ,yisheng.zhiwei_types as zhiweiTypes ,yisheng.yisheng_shanchang as yishengShanchang ,yisheng.yisheng_content as yishengContent ,yisheng.shangxia_types as shangxiaTypes ,yisheng.yisheng_clicknum as yishengClicknum ,yisheng.yisheng_delete as yishengDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.shiyong_types as shiyongTypes ,yonghu.yonghu_email as yonghuEmail
FROM yisheng_order a left JOIN yisheng yisheng ON a.yisheng_id = yisheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.yisheng_order_uuid_number like CONCAT('%',#{params.yishengOrderUuidNumber},'%')
and ( a.yisheng_id = #{params.yishengId} )
and ( a.yonghu_id = #{params.yonghuId} )
and UNIX_TIMESTAMP(a.guahao_time) >= UNIX_TIMESTAMP(#{params.guahaoTimeStart})
and UNIX_TIMESTAMP(a.guahao_time) <= UNIX_TIMESTAMP(#{params.guahaoTimeEnd})
and a.shijianduan_types = #{params.shijianduanTypes}
and a.yisheng_order_types = #{params.yishengOrderTypes}
and a.beizhu_content like CONCAT('%',#{params.beizhuContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yisheng_id IS NOT NULL
and yisheng.yisheng_uuid_number like CONCAT('%',#{params.yishengUuidNumber},'%')
and yisheng.yisheng_name like CONCAT('%',#{params.yishengName},'%')
and yisheng.yisheng_types = #{params.yishengTypes}
and yisheng.zhiwei_types = #{params.zhiweiTypes}
and yisheng.yisheng_shanchang like CONCAT('%',#{params.yishengShanchang},'%')
and yisheng.yisheng_content like CONCAT('%',#{params.yishengContent},'%')
and yisheng.shangxia_types = #{params.shangxiaTypes}
and yisheng.yisheng_clicknum >= #{params.yishengClicknumStart}
and yisheng.yisheng_clicknum <= #{params.yishengClicknumEnd}
and yisheng.yisheng_clicknum = #{params.yishengClicknum}
and yisheng.yisheng_delete >= #{params.yishengDeleteStart}
and yisheng.yisheng_delete <= #{params.yishengDeleteEnd}
and yisheng.yisheng_delete = #{params.yishengDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.shiyong_types = #{params.shiyongTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
